import { motion, AnimatePresence } from "framer-motion";
import { useState } from "react";
import convert from "color-convert";
import data from "./data/data";
import firstSongList from "./data/firstSongList";
import secondSongList from "./data/secondSongList";
import SectionOne from "./sections/SectionOne";
import SectionTwo from "./sections/SectionTwo";
import SectionThree from "./sections/SectionThree";
import SectionFour from "./sections/SectionFour";
import SectionSix from "./sections/SectionSix";
import SectionSeven from "./sections/SectionSeven";
import SectionEight from "./sections/SectionEight";
import SectionNine from "./sections/SectionNine";
import SectionTen from "./sections/SectionTen";
let albumCovers = require.context("../src/assets/albumCovers", true);
let sixAlbums = require.context("../src/assets/sixAlbums", true);

function App() {
  const [animState, setAnimState] = useState(9);
  const [firstSelectedSong, setFirstSelectedSong] = useState(firstSongList[0]);
  const [secondSelectedSong, setSecondSelectedSong] = useState(
    secondSongList[0]
  );

  const [currentHoverColor, setCurrentHoverColor] = useState("#000");
  const [currentSongObject, setCurrentSongObject] = useState(null);
  const advanceState = () => {
    setAnimState(animState + 1);
  };
  const previousState = () => {
    setAnimState(animState - 1);
  };

  //function to sort array of hex values by hue and brightness
  const sortHue = () => {
    let array = data.map((item) => item);
    let sortedArray = array.sort((a, b) => {
      let aHue = convert.hex.hsl(a.ProminentColor1)[0];
      let bHue = convert.hex.hsl(b.ProminentColor1)[0];
      let aBrightness = convert.hex.hsl(a.ProminentColor1)[2];
      let bBrightness = convert.hex.hsl(b.ProminentColor1)[2];
      if (aHue > bHue) {
        return 1;
      } else if (aHue < bHue) {
        return -1;
      } else if (aBrightness > bBrightness) {
        return 1;
      } else if (aBrightness < bBrightness) {
        return -1;
      } else {
        return 0;
      }
    });
    return sortedArray;
  };

  //function to sort array of songs by location
  const sortLocation = () => {
    let array = data.map((item) => item);
    let sortedArray = array.sort((a, b) => {
      let aLocation = a.Region;
      let bLocation = b.Region;
      if (aLocation > bLocation) {
        return 1;
      } else if (aLocation < bLocation) {
        return -1;
      } else {
        return 0;
      }
    });
    return sortedArray;
  };

  //function to sort array of songs by release year
  const sortYear = () => {
    let array = data.map((item) => item);
    let sortedArray = array.sort((a, b) => {
      let aYear = a.ReleaseYear;
      let bYear = b.ReleaseYear;
      if (aYear > bYear) {
        return 1;
      } else if (aYear < bYear) {
        return -1;
      } else {
        return 0;
      }
    });
    return sortedArray;
  };

  const sortedHexArray = sortHue();
  const sortedLocationArray = sortLocation();
  const sortedYearArray = sortYear();

  return (
    <div>
      {animState !== 1 && (
        <div
          className="z-100 absolute items-stretch content-center p-2 m-2 bg-white text-center rounded-full 
     border-gray-300 text-gray-300 hover:text-gray-900 hover:border-gray-900 border-2"
          onClick={previousState}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </div>
      )}
      <div
        className="flex w-screen min-h-screen items-center"
        style={{ fontVariantLigatures: "none" }}
      >
        <div className="w-full place-items-stretch">
          <AnimatePresence>
            {
              {
                1: <SectionOne advanceState={advanceState}></SectionOne>,
                2: (
                  <SectionTwo
                    advanceState={advanceState}
                    setFirstSelectedSong={setFirstSelectedSong}
                    sixAlbums={sixAlbums}
                    firstSongList={firstSongList}
                  ></SectionTwo>
                ),
                3: (
                  <SectionThree
                    advanceState={advanceState}
                    sixAlbums={sixAlbums}
                    selectedSong={firstSelectedSong}
                  ></SectionThree>
                ),
                4: (
                  <SectionFour
                    advanceState={advanceState}
                    sixAlbums={sixAlbums}
                    secondSongList={secondSongList}
                    setSecondSelectedSong={setSecondSelectedSong}
                  ></SectionFour>
                ),
                5: (
                  <SectionThree
                    advanceState={advanceState}
                    sixAlbums={sixAlbums}
                    selectedSong={secondSelectedSong}
                  ></SectionThree>
                ),
                // These Categories, So Obvious... Right?
                6: <SectionSix advanceState={advanceState}></SectionSix>,
                //However the secret is that
                7: <SectionSeven advanceState={advanceState}></SectionSeven>,
                8: <SectionEight advanceState={advanceState}></SectionEight>,
                9: <SectionNine advanceState={advanceState}></SectionNine>,
                10: <SectionTen advanceState={advanceState}></SectionTen>,
                11: (
                  <div className="flex h-screen">
                    <div className="text-5xl w-1/2 p-12 leading-none">
                      <p>
                        {" "}
                        <span className="font-serif">Genre</span> is a category
                        of music, literature or other forms of art <br></br>{" "}
                        based on some set of criteria.
                      </p>
                      <motion.p
                        initial={{ y: "-10%", opacity: 0 }}
                        animate={{ y: "0%", opacity: 1 }}
                        transition={{
                          ease: "easeIn",
                          duration: 0.8,
                          delay: 0.3,
                        }}
                        className="pt-4"
                      >
                        {" "}
                        It is a type of communication with socially-agreed-upon
                        conventions developed over time.
                      </motion.p>
                    </div>
                    <div>
                      <motion.div
                        className="select-none absolute bottom-0 left-0 m-12 text-8xl w-min hover:text-gray-200"
                        onClick={advanceState}
                      >
                        ☞
                      </motion.div>
                    </div>
                    <div className="w-1/2 bg-gray-50 p-12 overflow-scroll">
                      <div className="w-5/6 flex flex-row flex-wrap justify-evenly m-auto gap-2">
                        {data.map((e) => (
                          <div
                            className="w-24 h-24 bg-gray-300 border-gray-500"
                            onClick={advanceState}
                            style={{
                              backgroundImage: `url(${
                                albumCovers(`./${e.Cover}.png`).default
                              })`,
                              backgroundSize: "100%",
                            }}
                          ></div>
                        ))}
                      </div>
                    </div>
                  </div>
                ),
                12: (
                  <div className="flex h-screen">
                    <div className="text-5xl w-1/2 p-12 leading-none">
                      <p>
                        {" "}
                        <span
                          className="font-serif"
                          style={{
                            color: `${currentHoverColor}`,
                          }}
                        >
                          Genre
                        </span>{" "}
                        is a category of music, literature or other forms of art{" "}
                        <br></br> based on some set of criteria.
                      </p>
                      <p className="pt-4">
                        {" "}
                        It is a type of communication with socially-agreed-upon
                        conventions developed over time.
                      </p>
                      <div className="flex">
                        <motion.div
                          initial={{ y: "-10%", opacity: 0 }}
                          animate={{ y: "0%", opacity: 1 }}
                          transition={{
                            ease: "easeIn",
                            duration: 0.8,
                            delay: 0.3,
                          }}
                          className="pt-4"
                        >
                          {" "}
                          They can be{" "}
                        </motion.div>
                        <motion.div
                          initial={{ y: "-10%", opacity: 0 }}
                          animate={{ y: "0%", opacity: 1 }}
                          transition={{
                            ease: "easeIn",
                            duration: 0.8,
                            delay: 2,
                          }}
                          className="pt-4 pl-4"
                          style={{ color: `${currentHoverColor}` }}
                        >
                          {" "}
                          aesthetic,
                        </motion.div>
                        <AnimatePresence>
                          {currentSongObject && (
                            <motion.div
                              initial={{ y: "-10%", opacity: 0 }}
                              animate={{ y: "0%", opacity: 1 }}
                              exit={{ y: "-10%", opacity: 0 }}
                              transition={{
                                ease: "easeIn",
                                duration: 0.3,
                                delay: 0,
                              }}
                              className="w-64 h-8 text-sm z-50"
                            >
                              <div className="flex flex-col justify-between">
                                <div className="text-xl capitalize font-serif">
                                  {currentSongObject.Genre}
                                </div>
                                <div className="text-sm capitalize font-serif">
                                  {currentSongObject.SongName}
                                </div>
                              </div>
                            </motion.div>
                          )}
                        </AnimatePresence>
                      </div>
                      <div
                        className="select-none absolute bottom-0 left-0 m-12 text-8xl w-min hover:text-gray-200 font-display"
                        onClick={advanceState}
                      >
                        ☞
                      </div>
                    </div>
                    <div className="w-1/2 bg-gray-50 p-12 overflow-scroll">
                      <div className="w-5/6 flex flex-row flex-wrap justify-evenly m-auto gap-2">
                        {data.map((song) => {
                          return (
                            <>
                              <div className="w-24 h-24">
                                <motion.div
                                  className="w-24 h-24"
                                  initial={{ y: "0%", opacity: 1 }}
                                  animate={{ y: "0%", opacity: 0 }}
                                  transition={{
                                    ease: "easeOut",
                                    duration: 1,
                                    delay: 2,
                                  }}
                                  onClick={advanceState}
                                  style={{
                                    backgroundImage: `url(${
                                      albumCovers(`./${song.Cover}.png`).default
                                    })`,
                                    backgroundSize: "100%",
                                  }}
                                ></motion.div>
                              </div>
                              <div className="w-24 h-24 -ml-20 z-10">
                                <motion.div
                                  className="w-24 h-24 "
                                  initial={{ y: "0%", opacity: 0 }}
                                  animate={{ y: "0%", opacity: 1 }}
                                  transition={{
                                    ease: "easeOut",
                                    duration: 0.8,
                                    delay: 2,
                                  }}
                                  style={{
                                    backgroundColor: `${song.ProminentColor1}`,
                                  }}
                                  onMouseEnter={() => {
                                    setCurrentHoverColor(song.ProminentColor1);
                                    setCurrentSongObject(song);
                                  }}
                                  onMouseLeave={() => {
                                    setCurrentHoverColor("#000");
                                    setCurrentSongObject(null);
                                  }}
                                ></motion.div>
                              </div>
                            </>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                ),
                13: (
                  <div className="flex h-screen">
                    <div className="text-5xl w-1/2 p-12 leading-none">
                      <p>
                        {" "}
                        <span
                          className="font-serif"
                          style={{
                            color: `${currentHoverColor}`,
                          }}
                        >
                          Genre
                        </span>{" "}
                        is a category of music, literature or other forms of art{" "}
                        <br></br> based on some set of criteria.
                      </p>
                      <p className="pt-4">
                        {" "}
                        It is a type of communication with socially-agreed-upon
                        conventions developed over time.
                      </p>
                      <div className="flex flex-wrap">
                        <div className="pt-4"> They can be </div>
                        <div className="pt-4 pl-4"> aesthetic,</div>
                        <motion.div
                          initial={{ y: "-10%", opacity: 0 }}
                          animate={{ y: "0%", opacity: 1 }}
                          transition={{
                            ease: "easeIn",
                            duration: 0.8,
                            delay: 1,
                          }}
                          className=""
                          style={{ color: `${currentHoverColor}` }}
                        >
                          {" "}
                          communicative,
                        </motion.div>
                        <AnimatePresence>
                          {currentSongObject && (
                            <motion.div
                              initial={{ y: "-10%", opacity: 0 }}
                              animate={{ y: "0%", opacity: 1 }}
                              exit={{ y: "-10%", opacity: 0 }}
                              transition={{
                                ease: "easeIn",
                                duration: 0.3,
                                delay: 0,
                              }}
                              className="w-64 h-8 text-sm z-50"
                            >
                              <div className="flex flex-col justify-between">
                                <div className="text-xl capitalize font-serif">
                                  {currentSongObject.Genre}
                                </div>
                                <div className="text-sm capitalize font-serif">
                                  {currentSongObject.SongName}
                                </div>
                              </div>
                            </motion.div>
                          )}
                        </AnimatePresence>
                      </div>
                      <div
                        className=" select-none absolute bottom-0 left-0 m-12 text-8xl w-min hover:text-gray-200 font-display"
                        onClick={advanceState}
                      >
                        ☞
                      </div>
                    </div>
                    <div className="w-1/2 bg-gray-50 p-12 overflow-scroll">
                      <div className="w-5/6 flex flex-row flex-wrap justify-evenly m-auto gap-2">
                        {data.map((song, index) => {
                          return (
                            <>
                              <motion.div
                                initial={{ y: "0%", opacity: 1 }}
                                animate={{ y: "0%", opacity: 0 }}
                                transition={{
                                  ease: "easeOut",
                                  duration: 0.8,
                                  delay: 1,
                                }}
                                className="w-24 h-24"
                                style={{
                                  backgroundColor: `${song.ProminentColor1}`,
                                }}
                              ></motion.div>
                              <motion.div
                                initial={{ y: "0%", opacity: 0 }}
                                animate={{ y: "0%", opacity: 1 }}
                                transition={{
                                  ease: "easeOut",
                                  duration: 0.8,
                                  delay: 1.4,
                                }}
                                className="w-24 h-24 -ml-20 z-10"
                                style={{
                                  backgroundColor: `${sortedHexArray[index].ProminentColor1}`,
                                }}
                                onMouseEnter={() => {
                                  setCurrentHoverColor(
                                    sortedHexArray[index].ProminentColor1
                                  );
                                  setCurrentSongObject(sortedHexArray[index]);
                                }}
                                onMouseLeave={() => {
                                  setCurrentHoverColor("#000");
                                  setCurrentSongObject(null);
                                }}
                              ></motion.div>
                            </>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                ),
                14: (
                  <div className="flex h-screen">
                    <div className="text-5xl w-1/2 p-12 leading-none">
                      <p>
                        {" "}
                        <span
                          className="font-serif"
                          style={{
                            color: `${currentHoverColor}`,
                          }}
                        >
                          Genre
                        </span>{" "}
                        is a category of music, literature or other forms of art{" "}
                        <br></br> based on some set of criteria.
                      </p>
                      <p className="pt-4">
                        {" "}
                        It is a type of communication with socially-agreed-upon
                        conventions developed over time.
                      </p>
                      <div className="flex flex-wrap w-auto">
                        <div className="pt-4"> They can be </div>
                        <div className="pt-4 pl-4"> aesthetic,</div>
                        <div className=""> communicative, </div>
                        <motion.div
                          initial={{ y: "-10%", opacity: 0 }}
                          animate={{ y: "0%", opacity: 1 }}
                          transition={{
                            ease: "easeIn",
                            duration: 0.8,
                            delay: 1,
                          }}
                          className="pl-2"
                          style={{ color: `${currentHoverColor}` }}
                        >
                          {" "}
                          or functional.
                          <AnimatePresence>
                            {currentSongObject && (
                              <motion.div
                                initial={{ y: "-10%", opacity: 0 }}
                                animate={{ y: "0%", opacity: 1 }}
                                exit={{ y: "-10%", opacity: 0 }}
                                transition={{
                                  ease: "easeIn",
                                  duration: 0.3,
                                  delay: 0,
                                }}
                                className="w-64 h-8 text-sm z-50"
                              >
                                <div className="flex flex-col justify-between">
                                  <div className="text-xl capitalize font-serif">
                                    {currentSongObject.Region}
                                  </div>
                                  <div className="text-sm capitalize font-serif">
                                    {currentSongObject.Genre}
                                  </div>
                                </div>
                              </motion.div>
                            )}
                          </AnimatePresence>
                        </motion.div>
                      </div>
                      <div
                        className="select-none absolute bottom-0 left-0 m-12 text-8xl w-min hover:text-gray-200 font-display"
                        onClick={advanceState}
                      >
                        ☞
                      </div>
                    </div>
                    <div className="w-1/2 bg-gray-50 p-12 overflow-scroll">
                      <div className="w-5/6 flex flex-row flex-wrap justify-evenly m-auto gap-2">
                        {data.map((song, index) => {
                          return (
                            <>
                              <div className="w-24 h-24">
                                <motion.div
                                  initial={{ y: "0%", opacity: 1 }}
                                  animate={{ y: "0%", opacity: 0 }}
                                  transition={{
                                    ease: "easeOut",
                                    duration: 0.8,
                                    delay: 2.4,
                                  }}
                                  className="w-24 h-24"
                                  style={{
                                    backgroundColor: `${sortedHexArray[index].ProminentColor1}`,
                                  }}
                                ></motion.div>
                              </div>
                              <div className="w-24 h-24 -ml-20 z-10">
                                <motion.div
                                  initial={{ y: "0%", opacity: 0 }}
                                  animate={{ y: "0%", opacity: 1 }}
                                  transition={{
                                    ease: "easeOut",
                                    duration: 0.8,
                                    delay: 2.4,
                                  }}
                                  className="w-24 h-24"
                                  style={{
                                    backgroundColor: `${sortedLocationArray[index].ProminentColor1}`,
                                  }}
                                  onMouseEnter={() => {
                                    setCurrentHoverColor(
                                      sortedLocationArray[index].ProminentColor1
                                    );
                                    setCurrentSongObject(
                                      sortedLocationArray[index]
                                    );
                                  }}
                                  onMouseLeave={() => {
                                    setCurrentHoverColor("#000");
                                    setCurrentSongObject(null);
                                  }}
                                ></motion.div>
                              </div>
                            </>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                ),
                15: (
                  <div className="flex h-screen">
                    <div className="text-5xl w-1/2 p-12 leading-none">
