import { motion } from "framer-motion";

function SectionTwo({
  advanceState,
  sixAlbums,
  setFirstSelectedSong,
  firstSongList,
}) {
  return (
    <div className="mx-auto text-5xl w-max leading-snug place-items-stretch">
      <motion.div
        initial={{ y: "-20%", opacity: 0 }}
        animate={{ y: "0%", opacity: 1 }}
        transition={{ ease: "easeOut", duration: 1, delay: 1 }}
        className="italic font-serif"
      >
        for instance,
      </motion.div>
      <motion.div
        initial={{ y: "-20%", opacity: 0 }}
        animate={{ y: "0%", opacity: 1 }}
        transition={{ ease: "easeOut", duration: 1, delay: 2 }}
      >
        PICK A SONG FROM HERE
      </motion.div>
      <motion.div className="flex flex-row justify-between text-8x pt-8">
        {firstSongList.map((item, index) => {
          return (
            <motion.div
              initial={{ y: "-20%", opacity: 0 }}
              animate={{ y: "0%", opacity: 1 }}
              transition={{
                ease: "easeOut",
                duration: 1,
                delay: 3 + index * 0.2,
              }}
              className="w-36 h-36 bg-gray-300 opacity-50 hover:border-2"
              onClick={() => {
                setFirstSelectedSong(item);
                advanceState();
              }}
              style={{
                backgroundImage: `url(${
                  sixAlbums(`./${item.Cover}.png`).default
                })`,
                backgroundSize: "100%",
              }}
            ></motion.div>
          );
        })}
      </motion.div>
    </div>
  );
}

export default SectionTwo;
