  const getAverageColor = () => {
    let colorArray = [];
    data.forEach((song) => {
      average(albumCovers(`./${song.Cover}.png`).default, {
        format: "hex",
      }).then((color) => {
        colorArray.push(song.Cover + " " + color);
      });
    });
    return colorArray;
  };

  const colorArray = getAverageColor();
  console.log(colorArray);

  return (
    <div>
      <div className="flex w-screen min-h-screen items-center">
        <div className="w-full place-items-stretch">
