import { motion, AnimatePresence } from "framer-motion";
import { useState } from "react";
import convert from "color-convert";
import data from "./data/data";
import firstSongList from "./data/firstSongList";
import secondSongList from "./data/secondSongList";
import SectionOne from "./sections/SectionOne";
import SectionTwo from "./sections/SectionTwo";
import SectionThree from "./sections/SectionThree";
import SectionFour from "./sections/SectionFour";
import SectionSix from "./sections/SectionSix";
import SectionSeven from "./sections/SectionSeven";
import SectionEight from "./sections/SectionEight";
import SectionNine from "./sections/SectionNine";
import SectionTen from "./sections/SectionTen";
import SectionEleven from "./sections/SectionEleven";
import SectionTwelve from "./sections/SectionTwelve";
import SectionThirteen from "./sections/SectionThirteen";
import SectionFourteen from "./sections/SectionFourteen";
import SectionFifteen from "./sections/SectionFifteen";
import SectionSixteen from "./sections/SectionSixteen";
import SectionSeventeen from "./sections/SectionSeventeen";
import SectionEighteen from "./sections/SectionEighteen";

let sixAlbums = require.context("../src/assets/sixAlbums", true);

function App() {
  const [animState, setAnimState] = useState(18);
  const [firstSelectedSong, setFirstSelectedSong] = useState(firstSongList[0]);
  const [secondSelectedSong, setSecondSelectedSong] = useState(
    secondSongList[0]
  );

  const [currentHoverColor, setCurrentHoverColor] = useState("#000");
  const [currentSongObject, setCurrentSongObject] = useState(null);
  const advanceState = () => {
    setAnimState(animState + 1);
  };
  const previousState = () => {
    setAnimState(animState - 1);
  };

  //function to sort array of hex values by hue and brightness
  const sortHue = () => {
    let array = data.map((item) => item);
    let sortedArray = array.sort((a, b) => {
      let aHue = convert.hex.hsl(a.ProminentColor1)[0];
      let bHue = convert.hex.hsl(b.ProminentColor1)[0];
      let aBrightness = convert.hex.hsl(a.ProminentColor1)[2];
      let bBrightness = convert.hex.hsl(b.ProminentColor1)[2];
      if (aHue > bHue) {
        return 1;
      } else if (aHue < bHue) {
        return -1;
      } else if (aBrightness > bBrightness) {
        return 1;
      } else if (aBrightness < bBrightness) {
        return -1;
      } else {
        return 0;
      }
    });
    return sortedArray;
  };

  //function to sort array of songs by release year
  const sortYear = () => {
    let array = data.map((item) => item);
    let sortedArray = array.sort((a, b) => {
      let aYear = a.ReleaseYear;
      let bYear = b.ReleaseYear;
      if (aYear > bYear) {
        return 1;
      } else if (aYear < bYear) {
        return -1;
      } else {
        return 0;
      }
    });
    return sortedArray;
  };

  const sortedHexArray = sortHue();
  const sortedYearArray = sortYear();

  return (
    <div>
      {animState !== 1 && (
        <div
          className="z-100 absolute items-stretch content-center p-2 m-2 bg-white text-center rounded-full 
     border-gray-300 text-gray-300 hover:text-gray-900 hover:border-gray-900 border-2"
          onClick={previousState}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </div>
      )}
      <div
        className="flex w-screen min-h-screen items-center"
        style={{ fontVariantLigatures: "none" }}
      >
        <div className="w-full place-items-stretch">
          <AnimatePresence>
            {
              {
                1: <SectionOne advanceState={advanceState}></SectionOne>,
                2: (
                  <SectionTwo
                    advanceState={advanceState}
                    setFirstSelectedSong={setFirstSelectedSong}
                    sixAlbums={sixAlbums}
                    firstSongList={firstSongList}
                  ></SectionTwo>
                ),
                3: (
                  <SectionThree
                    advanceState={advanceState}
                    sixAlbums={sixAlbums}
                    selectedSong={firstSelectedSong}
                  ></SectionThree>
                ),
                4: (
                  <SectionFour
                    advanceState={advanceState}
                    sixAlbums={sixAlbums}
                    secondSongList={secondSongList}
