import { motion } from "framer-motion";
import NextButton from "../components/NextButton";

function SectionThree({ advanceState, firstSelectedSong, sixAlbums }) {
  return (
    <div>
      <div></div>
      <div className="mx-auto text-5xl md:text-4xl w-max leading-snug place-items-stretch">
        <motion.div
          initial={{ y: "-20%", opacity: 0 }}
          animate={{ y: "0%", opacity: 1 }}
          transition={{
            ease: "easeOut",
            duration: 1,
            delay: 1,
          }}
          className="flex flex-row space-x-8"
        >
          <div
            className="w-36 h-36 bg-gray-500 md:w-24 md:h-24"
            style={{
              backgroundImage: `url(${
                sixAlbums(`./${firstSelectedSong.Cover}.png`).default
              })`,
              backgroundSize: "100%",
            }}
          ></div>
          <div className="flex flex-col ">
            <motion.div className="font-serif">
              {firstSelectedSong.Genre}
            </motion.div>
            <div className="text-gray-300 text-3xl md:text-xl flex flex-col justify-end">
              {firstSelectedSong.SongName}
              <br></br>
              {firstSelectedSong.Artist} ({firstSelectedSong.ReleaseYear})
            </div>
          </div>
          <NextButton
            className="-mt-2"
            advanceState={advanceState}
            delay={2}
          ></NextButton>
        </motion.div>
      </div>
    </div>
  );
}

export default SectionThree;
